 * 1. BossBoss
 * 2. BossBoss
 * 3. Boss
 * 4. Boss10
 * 5. Boss
 * 6. Boss
 * 
 * 
 * - 100-500
 * - 
 * 
 * 
 * - Boss
 * - 10
 * - 
 * 
 * 
 * - FightRecords: 
 * - UserStats: 
 * - NftAwards: BossNFT
 */
contract BossCore {

    //  - 

    /** 
     * @dev Boss
     * @param bossId Boss ID
     * @param name Boss
     * @param maxHp Boss
     * @param description Boss
     * @param imageUrl BossURL
     */
    event BossCreated(uint256 bossId, string name, uint256 maxHp, string description, string imageUrl);

    /** 
     * @dev Boss
     * @param attacker 
     * @param bossId Boss ID
     * @param damage 
     * @param bossHpAfter Boss
     */
    event BossAttacked(address indexed attacker, uint256 bossId, uint256 damage, uint256 bossHpAfter);
    //
    /** 
     * @dev 
     * @param bossId Boss ID
     * @param skillName 
     * @param duration 
     */
    event SkillTriggered(uint256 bossId, string skillName, uint256 duration);

    /** 
     * @dev 
     * @param bossId Boss ID
     * @param skillName 
     */
    event SkillEnded(uint256 bossId, string skillName);

    /** 
     * @dev Boss
     * @param bossId Boss ID
     * @param topThree 
     */
    event BossDefeated(uint256 bossId, address[] topThree);

    // 

    /**
     * @dev 
     * @dev 
     */
    enum RecordType {
        Attack,      // 
        Skill,       // 
        SkillEnd     // 
    }

    /**
     * @dev Boss
     * @dev Boss
     */
    struct Boss {
        uint256 id;                       // Boss
        string name;                      // Boss
        string description;               // Boss
        uint256 maxHp;                    // 
        uint256 currentHp;                // 
        uint256 level;                    // Boss
        string imageUrl;                  // BossURL
        string goldNftUrl;                // NFTURL
        string silverNftUrl;              // NFTURL
        string bronzeNftUrl;              // NFTURL
        uint256 attackCount;              // 
        bool isActive;                    // 
        bool isDefeated;                  // 
        string skillName;                 // 
        uint256 respawnTime;              // Unix
        bool isRespawning;                // 
    }

    // 

    /**
     * @dev Boss
     * @dev Boss IDBoss
     */
    mapping(uint256 => Boss) public bosses;

    /**
     * @dev Boss ID
     * @dev BossID
     */
    uint256 public nextBossId;

    //  - 

    /** 
     * @dev WorldBossSystem
     */
    address public worldBossSystemContract;

    /** 
     * @dev FightRecords
     */
    address public fightRecordsContract;

    /** 
     * @dev UserStats
     */
    address public userStatsContract;

    /** 
     * @dev NftAwards NFT
     */
    address public nftAwardsContract;

    // 

    /**
     * @dev 
     * @dev 
     */
    uint256 private randomSeed;

    // 

    /**
     * @dev 
     */
    address public owner;

    /**
     * @dev 
     */
    modifier onlyOwner() {
        require(msg.sender == owner, "Not owner");
        _;
    }

    /**
     * @dev 
     * @dev 
     */
    modifier onlySystem() {
        require(
            msg.sender == fightRecordsContract ||
            msg.sender == userStatsContract ||
            msg.sender == nftAwardsContract,
            "Not authorized system contract"
        );
        _;
    }

    // 

    /**
     * @dev 
     * @dev 
     */
    constructor() {
        owner = msg.sender;
        randomSeed = uint256(keccak256(abi.encodePacked(block.timestamp, block.difficulty)));
    }

    //  - 

    /**
     * @dev 
     * @dev 
     * @param _fightRecordsContract FightRecords
     * @param _userStatsContract UserStats
     * @param _nftAwardsContract NftAwards
     */
    function setContractAddresses(
        address _fightRecordsContract,
        address _userStatsContract,
        address _nftAwardsContract
    ) external onlyOwner {
        fightRecordsContract = _fightRecordsContract;
        userStatsContract = _userStatsContract;
        nftAwardsContract = _nftAwardsContract;
    }

    /**
     * @dev WorldBossSystem
     * @param _worldBossSystemContract WorldBossSystem
     */
    function setWorldBossSystemContract(address _worldBossSystemContract) external onlyOwner {
        worldBossSystemContract = _worldBossSystemContract;
    }

    //  - Boss

    /**
     * @dev Boss
     * @dev Boss
     * @param name Boss
     * @param description Boss
     * @param maxHp Boss
     * @param level Boss
     * @param imageUrl BossURL
     * @param goldNftUrl NFTURL
     * @param silverNftUrl NFTURL
     * @param bronzeNftUrl NFTURL
     * @return bossId Boss ID
     */
    function createBoss(
        string memory name,
        string memory description,
        uint256 maxHp,
        uint256 level,
        string memory imageUrl,
        string memory goldNftUrl,
        string memory silverNftUrl,
        string memory bronzeNftUrl,
        string memory _skillName
    ) external returns (uint256) {
        // WorldBossSystem
        require(
            msg.sender == owner || 
            msg.sender == worldBossSystemContract || 
            msg.sender == fightRecordsContract || 
            msg.sender == userStatsContract || 
            msg.sender == nftAwardsContract,
            "Not authorized"
        );

        uint256 bossId = nextBossId++;
        Boss storage boss = bosses[bossId];

        boss.id = bossId;
        boss.name = name;
        boss.description = description;
        boss.maxHp = maxHp;
        boss.currentHp = maxHp;
        boss.level = level;
        boss.imageUrl = imageUrl;
        boss.goldNftUrl = goldNftUrl;
        boss.silverNftUrl = silverNftUrl;
        boss.bronzeNftUrl = bronzeNftUrl;
        boss.attackCount = 0;
        boss.isActive = true;
        boss.isDefeated = false;
        boss.skillName = _skillName;
        boss.respawnTime = 0;
        boss.isRespawning = false;

        emit BossCreated(bossId, name, maxHp, description, imageUrl);
        return bossId;
    }

    //  - 

    /**
     * @dev Boss
     * @dev Boss
     * @param bossId Boss ID
     * @return bool truefalse
     */
    function checkCanAttack(uint256 bossId) public returns (bool) {
        Boss storage boss = bosses[bossId];

        // 
        checkRespawn(bossId);

        require(boss.isActive, "Boss not active");
        require(!boss.isDefeated, "Boss already defeated");

        return true;
    }

    /**
     * @dev Boss
     * @dev Boss
     * @param bossId Boss ID
     */
    function checkRespawn(uint256 bossId) public {
        Boss storage boss = bosses[bossId];

        // Boss
        if (boss.isRespawning && block.timestamp >= boss.respawnTime) {
            // Boss
            boss.isDefeated = false;
            boss.isActive = true;
            boss.isRespawning = false;
            boss.currentHp = boss.maxHp;
            boss.attackCount = 0;
            boss.respawnTime = 0;

            emit BossCreated(boss.id, boss.name, boss.maxHp, boss.description, boss.imageUrl);
        }
    }

    /**
     * @dev Boss
     * @dev Boss
     */
    function checkAllRespawns() public {
        for (uint256 i = 0; i < nextBossId; i++) {
            checkRespawn(i);
        }
    }

    /**
     * @dev 
     * @dev 100-500
     * @return uint256 
     */
    function calculateDamage() public returns (uint256) {
        // 
        randomSeed = uint256(keccak256(abi.encodePacked(
            randomSeed,
            block.timestamp,
            block.difficulty,
            msg.sender,
            block.number
        )));

        // 100-500
        return (randomSeed % 401) + 100;
    }

    /**
     * @dev Boss
     * @dev Boss
     * @param bossId Boss ID
     * @param damage 
     * @return uint256 Boss
     */
    function updateBossHp(uint256 bossId, uint256 damage) public returns (uint256) {
        Boss storage boss = bosses[bossId];
        require(boss.isActive, "Boss not active");
        require(!boss.isDefeated, "Boss already defeated");

        // 
        uint256 newHp = boss.currentHp > damage ? boss.currentHp - damage : 0;
        boss.currentHp = newHp;
        boss.attackCount++;

        emit BossAttacked(msg.sender, bossId, damage, newHp);

        // 
        if (fightRecordsContract != address(0)) {
            // assembly
            (bool success, ) = fightRecordsContract.call(
                abi.encodeWithSignature(
                    "addAttackRecord(uint256,address,uint256,uint256)",
                    bossId,
                    msg.sender,
                    damage,
                    newHp
                )
            );
        }

        // 
        if (userStatsContract != address(0)) {
            // assembly
            (bool success, ) = userStatsContract.call(
                abi.encodeWithSignature(
                    "updateUserStats(address,uint256)",
                    msg.sender,
                    damage
                )
            );
        }

        // Boss
        if (newHp == 0) {
            boss.isDefeated = true;
            boss.isActive = false;
            // 10
            boss.respawnTime = block.timestamp + 10;
            boss.isRespawning = true;
        }

        return newHp;
    }



    //  - 

    /**
     * @dev Boss
     * @param bossId Boss ID
     * @return id Boss ID
     * @return name Boss
     * @return description Boss
     * @return maxHp 
     * @return currentHp 
     * @return level Boss
     * @return imageUrl BossURL
     * @return goldNftUrl NFTURL
     * @return silverNftUrl NFTURL
     * @return bronzeNftUrl NFTURL
     * @return attackCount 
     * @return isActive 
     * @return isDefeated 
     * @return skillName 
     */
    function getBossInfo(uint256 bossId) external view returns (
        uint256 id,
        string memory name,
        string memory description,
        uint256 maxHp,
        uint256 currentHp,
        uint256 level,
        string memory imageUrl,
        string memory goldNftUrl,
        string memory silverNftUrl,
        string memory bronzeNftUrl,
        uint256 attackCount,
        bool isActive,
        bool isDefeated,
        string memory skillName
    ) {
        Boss storage boss = bosses[bossId];
        return (
            boss.id,
            boss.name,
            boss.description,
            boss.maxHp,
            boss.currentHp,
            boss.level,
            boss.imageUrl,
            boss.goldNftUrl,
            boss.silverNftUrl,
            boss.bronzeNftUrl,
            boss.attackCount,
            boss.isActive,
            boss.isDefeated,
            boss.skillName
        );
    }

    /**
     * @dev Boss
     * @param bossId Boss ID
     * @return uint256 0-100
     */
    function getBossHpPercentage(uint256 bossId) external view returns (uint256) {
        Boss storage boss = bosses[bossId];
        if (boss.maxHp == 0) return 0;
        return (boss.currentHp * 100) / boss.maxHp;
    }

    /**
     * @dev Boss
     * @dev Boss ID
     * @return uint256[] Boss ID
     */
    function getActiveBosses() external view returns (uint256[] memory) {
        uint256 activeCount = 0;

        // Boss
        for (uint256 i = 0; i < nextBossId; i++) {
            Boss storage boss = bosses[i];
            if (boss.isActive && !boss.isDefeated && !boss.isRespawning) {
                activeCount++;
            }
        }

        // 
        uint256[] memory activeBosses = new uint256[](activeCount);
        uint256 index = 0;

        // Boss ID
        for (uint256 i = 0; i < nextBossId; i++) {
            Boss storage boss = bosses[i];
            if (boss.isActive && !boss.isDefeated && !boss.isRespawning) {
                activeBosses[index] = i;
                index++;
            }
        }

        return activeBosses;
    }
}

contracts/FightRecords.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

/**
 * @title FightRecords
 * @dev Boss
 * 
 * 
 * 1. 
 * 2. Boss
 * 3. 
 * 4. 30
 * 
 * 
 * - Attack: Boss
 * - Skill: Boss
 * - SkillEnd: Boss
 * 
 * 
 * - Boss
 * - 
 * -  gas 
 * 
 * 
 * - BossCore: 
 * - WorldBossSystem: 
 */
contract FightRecords {

    //  - 

    /** 
     * @dev 
     * @param bossId Boss ID
     * @param attacker 
     * @param damage 
     */
    event AttackRecordAdded(uint256 indexed bossId, address indexed attacker, uint256 damage);

    /** 
     * @dev 
     * @param bossId Boss ID
     * @param skillName 
     */
    event SkillRecordAdded(uint256 indexed bossId, string skillName);

    /** 
     * @dev 
     * @param bossId Boss ID
     * @param skillName 
     */
    event SkillEndRecordAdded(uint256 indexed bossId, string skillName);

    // 

    /**
     * @dev 
     * @dev 
     */
    enum RecordType {
        Attack,      // 
        Skill,       // 
        SkillEnd     // 
    }

    /**
     * @dev 
     * @dev 
     */
    struct AttackRecord {
        address attacker;        // address(0)
        uint256 timestamp;       // Unix
        uint256 damage;          // 0
        uint256 bossHpAfter;     // Boss
        RecordType recordType;   // 
        string skillName;        // 
    }

    // 

    /**
     * @dev Boss
     * @dev Boss IDBoss
     * @dev 
     */
    mapping(uint256 => AttackRecord[]) private bossRecords;

    /**
     * @dev 
     * @dev Boss IDBoss
     * @dev 
     */
    mapping(uint256 => uint256) private bossRecordCounts;

    // 

    /**
     * @dev BossCore
     * @dev BossCore
     */
    address public bossCoreContract;

    /**
     * @dev 
     */
    address public owner;

    // 

    /**
     * @dev 
     */
    modifier onlyOwner() {
        require(msg.sender == owner, "Not owner");
        _;
    }

    /**
     * @dev 
     */
    modifier onlySystem() {
        require(msg.sender == bossCoreContract, "Not authorized system contract");
        _;
    }

    // 

    /**
     * @dev 
     * @dev 
     */
    constructor() {
        owner = msg.sender;
    }

    //  - 

    /**
     * @dev BossCore
     * @param _bossCoreContract BossCore
     */
    function setBossCoreContract(address _bossCoreContract) external onlyOwner {
        bossCoreContract = _bossCoreContract;
    }

    //  - 

    /**
     * @dev 
     * @dev Boss
     * @param bossId Boss ID
     * @param attacker 
     * @param damage 
     * @param bossHpAfter Boss
     */
    function addAttackRecord(
        uint256 bossId,
        address attacker,
        uint256 damage,
        uint256 bossHpAfter
    ) external onlySystem {
        AttackRecord memory record = AttackRecord({
            attacker: attacker,
            timestamp: block.timestamp,
            damage: damage,
            bossHpAfter: bossHpAfter,
            recordType: RecordType.Attack,
            skillName: ""
        });

        bossRecords[bossId].push(record);
        bossRecordCounts[bossId]++;

        emit AttackRecordAdded(bossId, attacker, damage);
    }

    /**
     * @dev 
     * @dev Boss
     * @param bossId Boss ID
     * @param skillName 
     */
    function addSkillRecord(
        uint256 bossId,
        string memory skillName
    ) external onlySystem {
        AttackRecord memory record = AttackRecord({
            attacker: address(0), // 
            timestamp: block.timestamp,
            damage: 0,
            bossHpAfter: 0, // 
            recordType: RecordType.Skill,
            skillName: skillName
        });

        bossRecords[bossId].push(record);
        bossRecordCounts[bossId]++;

        emit SkillRecordAdded(bossId, skillName);
    }

    /**
     * @dev 
     * @dev Boss
     * @param bossId Boss ID
     * @param skillName 
     */
    function addSkillEndRecord(
        uint256 bossId,
        string memory skillName
    ) external onlySystem {
        AttackRecord memory record = AttackRecord({
            attacker: address(0),
            timestamp: block.timestamp,
            damage: 0,
            bossHpAfter: 0,
            recordType: RecordType.SkillEnd,
            skillName: skillName
        });

        bossRecords[bossId].push(record);
        bossRecordCounts[bossId]++;

        emit SkillEndRecordAdded(bossId, skillName);
    }

    //  - 

    /**
     * @dev Boss
     * @dev 30
     * @param bossId Boss ID
     * @param count 30
     * @return AttackRecord[] 
     */
    function getLatestAttackRecords(uint256 bossId, uint256 count) external view returns (AttackRecord[] memory) {
        require(count <= 30, "Can only query up to 30 records");

        uint256 totalRecords = bossRecordCounts[bossId];
        uint256 actualCount = count;

        if (totalRecords < count) {
            actualCount = totalRecords;
        }

        AttackRecord[] memory result = new AttackRecord[](actualCount);
        AttackRecord[] storage allRecords = bossRecords[bossId];

        uint256 startIndex = totalRecords - actualCount;

        for (uint256 i = 0; i < actualCount; i++) {
            result[i] = allRecords[startIndex + i];
        }

        return result;
    }

    /**
     * @dev Boss
     * @param bossId Boss ID
     * @return uint256 
     */
    function getRecordCount(uint256 bossId) external view returns (uint256) {
        return bossRecordCounts[bossId];
    }

    /**
     * @dev 
     * @param bossId Boss ID
     * @param recordIndex 0
     * @return AttackRecord 
     */
    function getRecord(uint256 bossId, uint256 recordIndex) external view returns (AttackRecord memory) {
        require(recordIndex < bossRecordCounts[bossId], "Record index out of range");
        return bossRecords[bossId][recordIndex];
    }
}

contracts/NftAwards.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract NftAwards is ERC721, ERC721URIStorage, Ownable {

    /**
     * @dev supportsInterface
     * @dev ERC721ERC721URIStoragesupportsInterface
     * @param interfaceId ID
     * @return bool 
     */
    function supportsInterface(bytes4 interfaceId) public view virtual override(ERC721, ERC721URIStorage) returns (bool) {
        return super.supportsInterface(interfaceId);
    }

    //  - 

    /** 
     * @dev NFT
     * @param winner 
     * @param tokenId NFT Token ID
     * @param awardType "Gold""Silver""Bronze"
     */
    event AwardMinted(address indexed winner, uint256 indexed tokenId, string awardType);

    /** 
     * @dev Boss
     * @param bossId Boss ID
     * @param goldWinner 
     * @param silverWinner 
     * @param bronzeWinner 
     */
    event BossAwardsDistributed(uint256 bossId, address goldWinner, address silverWinner, address bronzeWinner);

    // 

    /**
     * @dev 
     * @dev NFT
     */
    enum AwardType {
        Gold,       //  - 
        Silver,     //  - 
        Bronze      //  - 
    }

    // 

    /**
     * @dev NFTURI
     * @dev NFTURI
     * @dev GoldURISilverURIBronzeURI
     */
    mapping(AwardType => string) private awardURIs;

    /**
     * @dev Boss
     * @dev Boss IDBoss
     * @dev Boss
     */
    mapping(uint256 => bool) private bossAwardsDistributed;

    // 

    /**
     * @dev NFT Token ID
     * @dev 1NFTID
     */
    uint256 private nextTokenId;

    /**
     * @dev BossCore
     * @dev NFT
     */
    address public bossCoreContract;

    /**
     * @dev WorldBossSystem
     * @dev NFT
     */
    address public worldBossSystemContract;

    // 

    /**
     * @dev 
     * @dev NFT
     */
    modifier onlySystem() {
        require(
            msg.sender == bossCoreContract || 
            msg.sender == worldBossSystemContract,
            "Not authorized system contract"
        );
        _;
    }

    //  - 

    /**
     * @dev WorldBossSystem
     * @param _worldBossSystemContract WorldBossSystem
     */
    function setWorldBossSystemContract(address _worldBossSystemContract) external onlyOwner {
        worldBossSystemContract = _worldBossSystemContract;
    }

    // 

    /**
     * @dev 
     * @dev NFTToken ID
     * @dev WorldBossAwards (WBA)
     */
    constructor() ERC721("WorldBossAwards", "WBA") {
        nextTokenId = 1;
    }

    //  - 

    /**
     * @dev BossCore
     * @dev 
     * @dev BossCoreNFT
     * @param _bossCoreContract BossCore
     */
    function setBossCoreContract(address _bossCoreContract) external onlyOwner {
        bossCoreContract = _bossCoreContract;
    }

    /**
     * @dev NFTURI
     * @dev URI
     * @dev URIIPFSJSON
     * @param awardType Gold/Silver/Bronze
     * @param uri URI
     */
    function setAwardURI(AwardType awardType, string calldata uri) external onlyOwner {
        awardURIs[awardType] = uri;
    }

    //  - NFT

    /**
     * @dev NFTURI
     * @dev NFT
     * @dev ERC721_safeMint_setTokenURI
     * @param to NFT
     * @param awardType Gold/Silver/Bronze
     * @return tokenId NFT Token ID
     */
    function mintAward(address to, AwardType awardType) public onlySystem returns (uint256) {
        uint256 tokenId = nextTokenId++;
        string memory uri = awardURIs[awardType];

        _safeMint(to, tokenId);
        _setTokenURI(tokenId, uri);

        string memory awardTypeStr;
        if (awardType == AwardType.Gold) {
            awardTypeStr = "Gold";
        } else if (awardType == AwardType.Silver) {
            awardTypeStr = "Silver";
        } else {
            awardTypeStr = "Bronze";
        }

        emit AwardMinted(to, tokenId, awardTypeStr);
        return tokenId;
    }

    /**
     * @dev NFTURI
     * @dev NFTURI
     * @param to NFT
     * @param awardType Gold/Silver/Bronze
     * @param uri NFTURI
     * @return tokenId NFT Token ID
     */
    function mintAwardWithUri(address to, AwardType awardType, string calldata uri) public onlySystem returns (uint256) {
        uint256 tokenId = nextTokenId++;

        _safeMint(to, tokenId);
        _setTokenURI(tokenId, uri);

        string memory awardTypeStr;
        if (awardType == AwardType.Gold) {
            awardTypeStr = "Gold";
        } else if (awardType == AwardType.Silver) {
            awardTypeStr = "Silver";
        } else {
            awardTypeStr = "Bronze";
        }

        emit AwardMinted(to, tokenId, awardTypeStr);
        return tokenId;
    }

    /**
     * @dev Boss
     * @dev BossNFT
     * @dev Boss
     * @param bossId Boss IDBoss
     * @param goldWinner 
     * @param silverWinner 
     * @param bronzeWinner 
     */
    function distributeBossAwards(
        uint256 bossId,
        address goldWinner,
        address silverWinner,
        address bronzeWinner
    ) external onlySystem {
        require(!bossAwardsDistributed[bossId], "Awards already distributed for this boss");

        // NFT
        if (goldWinner != address(0)) {
            mintAward(goldWinner, AwardType.Gold);
        }
        if (silverWinner != address(0)) {
            mintAward(silverWinner, AwardType.Silver);
        }
        if (bronzeWinner != address(0)) {
            mintAward(bronzeWinner, AwardType.Bronze);
        }

        bossAwardsDistributed[bossId] = true;

        emit BossAwardsDistributed(bossId, goldWinner, silverWinner, bronzeWinner);
    }

    //  - 

    /**
     * @dev Boss
     * @param bossId Boss ID
     * @return bool truefalse
     */
    function areBossAwardsDistributed(uint256 bossId) external view returns (bool) {
        return bossAwardsDistributed[bossId];
    }

    //  - ERC721

    /**
     * @dev NFT
     * @dev _burnERC721URIStorage
     * @dev 
     * @param tokenId NFT Token ID
     */
    function _burn(uint256 tokenId) internal override(ERC721, ERC721URIStorage) {
        super._burn(tokenId);
    }

    /**
     * @dev NFTURI
     * @dev tokenURIERC721URIStorage
     * @param tokenId NFT Token ID
     * @return string NFTURI
     */
    function tokenURI(uint256 tokenId) public view override(ERC721, ERC721URIStorage) returns (string memory) {
        return super.tokenURI(tokenId);
    }
}

contracts/UserStats.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

/**
 * @title UserStats
 * @dev Boss
 * 
 * 
 * 1. Boss
 * 2. Boss
 * 3. Boss
 * 4. 
 * 
 * 
 * - BossCore: 
 * - WorldBossSystem: NFT
 */
contract UserStats {
    //  - 

    /** 
     * @dev 
     * @param user 
     * @param totalDamage Boss
     * @param attackCount Boss
     */
    event UserStatsUpdated(address indexed user, uint256 totalDamage, uint256 attackCount);

    /** 
     * @dev 
     * @param bossId Boss ID
     * @param goldWinner 
     * @param silverWinner 
     * @param bronzeWinner 
     */
    event TopThreeCalculated(uint256 bossId, address goldWinner, address silverWinner, address bronzeWinner);

    /**
     * @dev 
     * Boss
     */
    struct UserStat {
        uint256 attackCount;      // 
        uint256 totalDamage;      // 
        uint256 lastAttackTime;   // Unix
    }

    /**
     * @dev 
     * 
     */
    struct RankedUser {
        address userAddress;      // 
        uint256 totalDamage;      // 
    }

    //  - Boss ID

    /**
     * @dev Boss
     * @dev Boss ID
     * @dev Boss
     */
    mapping(uint256 => mapping(address => UserStat)) private bossUserStats;

    /**
     * @dev Boss
     * @dev Boss IDBoss
     * @dev 
     */
    mapping(uint256 => address[]) private bossParticipants;

    /**
     * @dev 
     * @dev Boss ID
     * @dev 
     */
    mapping(uint256 => mapping(address => bool)) private isParticipant;

    // 

    /** 
     * @dev BossCore
     * @dev BossCore
     */
    address public bossCoreContract;

    /** 
     * @dev WorldBossSystem
     * @dev WorldBossSystem
     */
    address public worldBossSystemContract;

    /** 
     * @dev 
     * @dev 
     */
    address public owner;

    // 

    /**
     * @dev 
     * @dev 
     */
    modifier onlyOwner() {
        require(msg.sender == owner, "Not owner");
        _;
    }

    /**
     * @dev 
     * @dev BossCoreWorldBossSystem
     */
    modifier onlySystem() {
        require(
            msg.sender == bossCoreContract || 
            msg.sender == worldBossSystemContract,
            "Not authorized system contract"
        );
        _;
    }

    // 

    /**
     * @dev 
     */
    constructor() {
        owner = msg.sender;
    }

    //  - 

    /**
     * @dev BossCore
     * @dev 
     * @param _bossCoreContract BossCore
     */
    function setBossCoreContract(address _bossCoreContract) external onlyOwner {
        bossCoreContract = _bossCoreContract;
    }

    /**
     * @dev WorldBossSystem
     * @param _worldBossSystemContract WorldBossSystem
     */
    function setWorldBossSystemContract(address _worldBossSystemContract) external onlyOwner {
        worldBossSystemContract = _worldBossSystemContract;
    }

    //  - 

    /**
     * @dev 
     * @dev BossCoreBoss
     * @dev 
     * @param bossId Boss IDBoss
     * @param user 
     * @param damage 
     */
    function updateUserStats(uint256 bossId, address user, uint256 damage) external onlySystem {
        UserStat storage userStat = bossUserStats[bossId][user];

        // 
        if (!isParticipant[bossId][user]) {
            bossParticipants[bossId].push(user);
            isParticipant[bossId][user] = true;
        }

        // 
        userStat.attackCount++;
        userStat.totalDamage += damage;
        userStat.lastAttackTime = block.timestamp;

        emit UserStatsUpdated(user, userStat.totalDamage, userStat.attackCount);
    }

    /**
     * @dev 
     * @param bossId Boss ID
     * @param user 
     * @return attackCount 
     * @return totalDamage 
     * @return lastAttackTime 
     */
    function getUserStats(uint256 bossId, address user) external view returns (
        uint256 attackCount,
        uint256 totalDamage,
        uint256 lastAttackTime
    ) {
        UserStat storage userStat = bossUserStats[bossId][user];
        return (
            userStat.attackCount,
            userStat.totalDamage,
            userStat.lastAttackTime
        );
    }

    /**
     * @dev Boss
     * @dev BossCore
     * @param bossId Boss ID
     * @return 0-100
     */
    function getBossHpPercentage(uint256 bossId) external view returns (uint256) {
        return 100;
    }

    /**
     * @dev Boss
     * @dev 
     * @dev NFT
     * @param bossId Boss ID
     * @return topThree  [, , ]
     */
    function calculateTopThree(uint256 bossId) external onlySystem returns (address[3] memory) {
        address[] storage participants = bossParticipants[bossId];
        uint256 participantCount = participants.length;

        // topDamages[0]
        address[3] memory topThree;
        uint256[3] memory topDamages;

        // 
        for (uint256 i = 0; i < participantCount; i++) {
            address user = participants[i];
            uint256 damage = bossUserStats[bossId][user].totalDamage;

            // 
            if (damage > topDamages[0]) {
                // 
                topDamages[2] = topDamages[1];
                topThree[2] = topThree[1];

                topDamages[1] = topDamages[0];
                topThree[1] = topThree[0];

                topDamages[0] = damage;
                topThree[0] = user;
            } else if (damage > topDamages[1]) {
                // 
                topDamages[2] = topDamages[1];
                topThree[2] = topThree[1];

                topDamages[1] = damage;
                topThree[1] = user;
            } else if (damage > topDamages[2]) {
                // 
                topDamages[2] = damage;
                topThree[2] = user;
            }
        }

        emit TopThreeCalculated(bossId, topThree[0], topThree[1], topThree[2]);
        return topThree;
    }

    /**
     * @dev Boss
     * @param bossId Boss ID
     * @return 
     */
    function getParticipantCount(uint256 bossId) external view returns (uint256) {
        return bossParticipants[bossId].length;
    }

    /**
     * @dev 
     * @param bossId Boss ID
     * @return 
     */
    function getAllParticipants(uint256 bossId) external view returns (address[] memory) {
        return bossParticipants[bossId];
    }

    /**
     * @dev Boss
     * @dev Boss
     * @param bossId Boss ID
     * @return totalDamage 
     */
    function getTotalDamage(uint256 bossId) external view returns (uint256) {
        address[] storage participants = bossParticipants[bossId];
        uint256 totalDamage = 0;

        for (uint256 i = 0; i < participants.length; i++) {
            totalDamage += bossUserStats[bossId][participants[i]].totalDamage;
        }

        return totalDamage;
    }
}

contracts/WorldBossSystem.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import "./BossCore.sol";
import "./FightRecords.sol";
import "./UserStats.sol";
import "./NftAwards.sol";

/**
 * @title WorldBossSystem
 * @dev Boss
 * 
 * 
 * 
 *                    WorldBossSystem                        
 *                orchestrates            
 * 
 *                                                   
 *                                                   
 *     
 *     BossCore         FightRecords         UserStats    
 *  Boss                   
 *     
 *                              
 *                              
 *                   
 *                      NftAwards     
 *                    NFT      
 *                   
 * 
 * 
 * 1. 
 * 2. BossBoss
 * 3. 
 * 4. BossNFT
 * 5. 
 * 
 * 
 * 1. WorldBossSystem
 * 2. initialize()
 * 3. createAndStartBossFight()Boss
 * 4. attack()
 * 5. BossNFT
 * 6. 10Boss
 * 
 * 
 * - 
 * - --
 * - 
 */
contract WorldBossSystem {

    //  - 

    /** 
     * @dev 
     */
    event SystemInitialized();

    /** 
     * @dev Boss
     * @param bossId Boss ID
     * @param bossName Boss
     */
    event BossFightStarted(uint256 bossId, string bossName);

    /** 
     * @dev Boss
     * @param bossId Boss ID
     * @param topThree 
     */
    event BossFightEnded(uint256 bossId, address[3] topThree);

    /** 
     * @dev 
     * @param user 
     * @param bossId Boss ID
     * @param damage 
     */
    event UserAttacked(address indexed user, uint256 bossId, uint256 damage);

    //  - 

    /**
     * @dev BossCore
     * @dev Boss
     */
    BossCore public bossCore;

    /**
     * @dev FightRecords
     * @dev 
     */
    FightRecords public fightRecords;

    /**
     * @dev UserStats
     * @dev 
     */
    UserStats public userStats;

    /**
     * @dev NftAwards
     * @dev NFT
     */
    NftAwards public nftAwards;

    // 

    /**
     * @dev Boss ID
     * @dev Boss
     */
    uint256 public activeBossId;

    /**
     * @dev 
     * @dev Boss
     */
    address public owner;

    // 

    /**
     * @dev 
     */
    modifier onlyOwner() {
        require(msg.sender == owner, "Not owner");
        _;
    }

    // 

    /**
     * @dev 
     * @dev 
     */
    constructor() {
        owner = msg.sender;
    }

    //  - 

    /**
     * @dev 
     * @dev 
     * @param _bossCore BossCore
     * @param _fightRecords FightRecords
     * @param _userStats UserStats
     * @param _nftAwards NftAwards
     */
    function initialize(
        address _bossCore,
        address _fightRecords,
        address _userStats,
        address _nftAwards
    ) external onlyOwner {
        // 
        bossCore = BossCore(_bossCore);
        fightRecords = FightRecords(_fightRecords);
        userStats = UserStats(_userStats);
        nftAwards = NftAwards(_nftAwards);

        emit SystemInitialized();
    }

    //  - Boss

    /**
     * @dev Boss
     * @dev BossBoss
     * @param name Boss
     * @param description Boss
     * @param maxHp Boss
     * @param level Boss
     * @param imageUrl BossURL
     * @param goldNftUrl NFTURL
     * @param silverNftUrl NFTURL
     * @param bronzeNftUrl NFTURL
     * @return uint256 Boss ID
     */
    function createAndStartBossFight(
        string memory name,
        string memory description,
        uint256 maxHp,
        uint256 level,
        string memory imageUrl,
        string memory goldNftUrl,
        string memory silverNftUrl,
        string memory bronzeNftUrl,
        string memory skillName
    ) external onlyOwner returns (uint256) {
        // Boss
        uint256 bossId = bossCore.createBoss(
            name, 
            description, 
            maxHp, 
            level, 
            imageUrl, 
            goldNftUrl, 
            silverNftUrl, 
            bronzeNftUrl,
            skillName
        );

        // Boss
        activeBossId = bossId;

        emit BossFightStarted(bossId, name);
        return bossId;
    }



    //  - 

    /**
     * @dev Boss
     * @dev activeBossId
     * 
     * 
     * 1. Boss
     * 2. 
     * 3. Boss
     * 4. 
     * 5. 
     * 6. 
     * 7. Boss
     */
    function attack() external {
        attackBoss(activeBossId);
    }

    /**
     * @dev Boss
     * @dev 
     * @param bossId Boss ID
     */
    function attackBoss(uint256 bossId) public {
        // Boss
        require(bossCore.checkCanAttack(bossId), "Boss cannot be attacked now");

        // 
        uint256 damage = bossCore.calculateDamage();

        // Boss
        uint256 bossHpAfter = bossCore.updateBossHp(bossId, damage);

        // Boss
        if (bossHpAfter == 0) {
            endBossFight(bossId);
        }

        emit UserAttacked(msg.sender, bossId, damage);
    }

    //  - 

    /**
     * @dev Boss
     * @dev NFT
     * @param bossId Boss ID
     */
    function endBossFight(uint256 bossId) internal {
        // 
        address[3] memory topThree = userStats.calculateTopThree(bossId);

        // BossNFT URL
        (
            ,
            ,
            ,
            ,
            ,
            ,
            ,
            string memory goldNftUrl,
            string memory silverNftUrl,
            string memory bronzeNftUrl,
            ,
            ,
            ,

        ) = bossCore.getBossInfo(bossId);

        // NFTBossNFTURL
        if (topThree[0] != address(0)) {
            nftAwards.mintAwardWithUri(topThree[0], NftAwards.AwardType.Gold, goldNftUrl);
        }
        if (topThree[1] != address(0)) {
            nftAwards.mintAwardWithUri(topThree[1], NftAwards.AwardType.Silver, silverNftUrl);
        }
        if (topThree[2] != address(0)) {
            nftAwards.mintAwardWithUri(topThree[2], NftAwards.AwardType.Bronze, bronzeNftUrl);
        }

        emit BossFightEnded(bossId, topThree);
    }

    //  - 

    /**
     * @dev Boss
     * @return id Boss ID
     * @return name Boss
     * @return description Boss
     * @return maxHp 
     * @return currentHp 
     * @return level Boss
     * @return imageUrl BossURL
     * @return goldNftUrl NFTURL
     * @return silverNftUrl NFTURL
     * @return bronzeNftUrl NFTURL
     * @return attackCount 
     * @return isActive 
     * @return isDefeated 
     * @return skillName 
     */
    function getActiveBossStatus() external view returns (
        uint256 id,
        string memory name,
        string memory description,
        uint256 maxHp,
        uint256 currentHp,
        uint256 level,
        string memory imageUrl,
        string memory goldNftUrl,
        string memory silverNftUrl,
        string memory bronzeNftUrl,
        uint256 attackCount,
        bool isActive,
        bool isDefeated,
        string memory skillName
    ) {
        return bossCore.getBossInfo(activeBossId);
    }

    /**
     * @dev Boss
     * @return uint256 0-100
     */
    function getBossHpPercentage() external view returns (uint256) {
        return bossCore.getBossHpPercentage(activeBossId);
    }

    /**
     * @dev Boss
     * @param count 30
     * @return FightRecords.AttackRecord[] 
     */
    function getLatestAttackRecords(uint256 count) external view returns (FightRecords.AttackRecord[] memory) {
        return fightRecords.getLatestAttackRecords(activeBossId, count);
    }

    /**
     * @dev Boss
     * @param user 
     * @return attackCount 
     * @return totalDamage 
     * @return lastAttackTime 
     */
    function getUserStats(address user) external view returns (
        uint256 attackCount,
        uint256 totalDamage,
        uint256 lastAttackTime
    ) {
        return userStats.getUserStats(activeBossId, user);
    }

    /**
     * @dev Boss
     * @return uint256 
     */
    function getParticipantCount() external view returns (uint256) {
        return userStats.getParticipantCount(activeBossId);
    }

    //  - 

    /**
     * @dev NFTURI
     * @param awardType 0=Gold, 1=Silver, 2=Bronze
     * @param uri URI
     */
    function setAwardURI(uint256 awardType, string calldata uri) external onlyOwner {
        nftAwards.setAwardURI(NftAwards.AwardType(awardType), uri);
    }
}

